import React from 'react';
import PropTypes from 'prop-types';
import { 
    Panel, Cell, List, 
    PanelHeader, Group, Div
} from '@vkontakte/vkui';

class Items extends React.Component {
	render() {
		let { id, items } = this.props

		return (
			<Panel id={id}>
				<PanelHeader>Товары</PanelHeader>
				<Group>
					<List>
						{
							items.length > 0 && items.map((item, index) => (
								<Cell 
									key={index}
									data-to="item" 
									onClick={(e) => this.props.go(e, { itemId : item.id })}
									before={
										<img 
										alt=""
											style={{ 
												width: 40, 
												height : 40, 
												margin : 10 
											}} 
											src={item.thumb_photo}
										/>
									}
									multiline
									description={item.description}
								>
								{item.title}, {item.price.amount} {item.price.currency.name}
								</Cell>
							))
						}
						{
							items.length === 0 &&
							<Div>
								Хм, мы не нашли товаров.
							</Div>
						}
					</List>
				</Group>
			</Panel>
		);
	}
}

Items.propTypes = {
	id: PropTypes.string.isRequired
};

export default Items;
