import React from 'react';
import PropTypes from 'prop-types';

import {
	Panel,PanelHeader,
	Button,
	Group, Cell, Div, View,
	Avatar,
	ModalRoot, ModalCard,
	FormLayout,
} from '@vkontakte/vkui';

import Icon56MoneyTransferOutline from '@vkontakte/icons/dist/24/done';
import Dostavka from './Dostavka';

const INFO = 'money-send';

class Home extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      activeModal: null,
      modalHistory: []
    };

    this.modalBack = () => {
      this.setActiveModal(this.state.modalHistory[this.state.modalHistory.length - 2]);
    };
  }

  setActiveModal(activeModal) {
    activeModal = activeModal || null;
    let modalHistory = this.state.modalHistory ? [...this.state.modalHistory] : [];

    if (activeModal === null) {
      modalHistory = [];
    } else if (modalHistory.indexOf(activeModal) !== -1) {
      modalHistory = modalHistory.splice(0, modalHistory.indexOf(activeModal) + 1);
    } else {
      modalHistory.push(activeModal);
    }

    this.setState({
      activeModal,
      modalHistory
    });
  };

  render() {
    const modal = ({ go}) =>(
      <ModalRoot activeModal={this.state.activeModal}>
        <ModalCard
          id={INFO}
          onClose={() => this.setActiveModal(null)}
          icon={<Icon56MoneyTransferOutline />}
          title="Сошьем вашу худи за три недели!"
          caption="Доставка займёт до 6 рабочих дней"
          actions={[{
            title: 'Подробнее о доставке',
            type: 'primary',
            action: () => {Dostavka}
          }]}
        >
        </ModalCard>
      </ModalRoot>
    );

    return (
      <View activePanel="modals" modal={modal}>
        <Panel id="modals">
          <PanelHeader>vkApp</PanelHeader>

          <Group>
            <FormLayout>
              <Button size="xl" level="secondary" onClick={() => this.setActiveModal(INFO)}>
                  Информация
              </Button>
            </FormLayout>
          </Group>
        </Panel>
      </View>
    );
  }
}

Home.propTypes = {
	id: PropTypes.string.isRequired,
	go: PropTypes.func.isRequired,
	fetchedUser: PropTypes.shape({
		photo_200: PropTypes.string,
		first_name: PropTypes.string,
		last_name: PropTypes.string,
		city: PropTypes.shape({
			title: PropTypes.string,
		}),
	}),
};

export default Home;
