import React from 'react';
import { View } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

import connect from '@vkontakte/vkui-connect-promise';

import Home from './panels/Home'
import Info from './panels/Info'

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state={
            activePanel: "home",
            fetchedUser: null,
            eruda: false
        }
    }

    componentDidMount () {
        connect.send("VKWebAppGetUserInfo", {}).then(e => this.setState({fetchedUser: e.data}))
    }

    eruda = async () => {
        await this.setState({eruda: !this.state.eruda})
        this.state.eruda ? window.eruda.init() : window.eruda.destroy()
    }

    go = e => {
        this.setState({activePanel: e.currentTarget.dataset.to})
    }

    render() {
        return (
            <View activePanel={this.state.activePanel}>
                <Home id="home" fetchedUser={this.state.fetchedUser} go={this.go} eruda={this.eruda} />
                <Info id="info" go={this.go} />
            </View>
        )
    }
}

export default App;
