import React from 'react';
import PropTypes from 'prop-types';
import {Panel, PanelHeader, FixedLayout, Div, Button, Header, Gallery} from '@vkontakte/vkui';


class Item extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        let {id} = this.props
        return (
            <Panel id={id}>
                <PanelHeader>Товар</PanelHeader>
                <Header>{item.title}</Header>
                <Gallery
                    slideWidth="100%"
                    align="right"
                    style={{ height: 350 }}
                >
                    {
                        item.photo.map((photo, index) => (
                            <img key={index} scr={photo.size[photo.sizes.length - 1]}></img>
                        ))
                    }
                </Gallery>
                <FixedLayout vertical="bottom">
                    <Div>
                        <Button size="xl" level="commerce">Купить</Button>
                    </Div>
                </FixedLayout>
            </Panel>
        );
    }
}

Item.propTypes = {
    id: PropTypes.string.isRequired,
    go: PropTypes.func.isRequired
};

export default Item;
