import React from 'react';
import PropTypes from 'prop-types';
import {
        Panel, PanelHeader, FixedLayout, Div, 
        Button, Header, Gallery, Group, HeaderButton
    } from '@vkontakte/vkui';

import Icon24Back from '@vkontakte/icons/dist/24/back';


class Item extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            slideIndex: 0,
            imageHeight: 350
        }
    }

    render() {
        let {id, item} = this.props
        return (
            <Panel id={id}>
                <PanelHeader
                    left={<HeaderButton data-to="items" onClick={(e)=>this.props.go(e)}>
                        <Icon24Back/>
                    </HeaderButton>}
                >Товар</PanelHeader>
                <Header>{item.title} <b>{item.price.text}</b></Header>
                <Gallery
                    slideWidth="100%"
                    style={{ height: this.state.imageHeight, textAlign: 'center'}}
                    bullets="dark"
                >
                    {
                        item.photo.map((photo, index) => (
                            <div key={index} style={{width: '100%', height: '100%', backgroundColor: '#fff'}}>
                                <img
                                    alt=""
                                    style={{
                                        maxHeight: this.state.imageHeight
                                    }}
                                    scr={photo.sizes[photo.sizes.length - 1].url}
                                >
                                </img>
                            </div>
                        ))
                    }
                </Gallery>
                <Group>
                    <Div>
                        {item.description}
                    </Div>
                </Group>
                <FixedLayout vertical="bottom">
                    <Div>
                        <Button size="xl" level="commerce">Купить</Button>
                    </Div>
                </FixedLayout>
            </Panel>
        );
    }
}

Item.propTypes = {
    id: PropTypes.string.isRequired,
    go: PropTypes.func.isRequired
};

export default Item;
