import React from 'react';
import connect from '@vkontakte/vk-connect';
import { View } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

import Items from './panels/Items';
import Item from './panels/Item';

class App extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			activePanel : 'items',
			authToken : null,
			items : [
				{
                    "id": 250409,
                    "owner_id": -124527492,
                    "title": "Persik",
                    "description": "A majestic furball who adores to sleep, to purr, and to play with a computer mouse.",
                    "price": {
                        "amount": "100000",
                        "currency": {
                            "id": 643,
                            "name": "RUB"
                        },
                        "text": "1,000 rub."
                    },
                    "category": {
                        "id": 1001,
                        "name": "Cats",
                        "section": {
                            "id": 10,
                            "name": "Pets"
                        }
                    },
                    "date": 1467722904,
                    "thumb_photo": "https://pp.vk.me/c631229/v631229852/3b6f3/SQ607FYCmy4.jpg",
                    "availability": 0
                },
                {
                    "id": 250407,
                    "owner_id": -124527492,
                    "title": "Spotty",
                    "description": "A tail wagging champion, true friend and never-failing warmer.",
                    "price": {
                        "amount": "100000",
                        "currency": {
                            "id": 643,
                            "name": "RUB"
                        },
                        "text": "1,000 rub."
                    },
                    "category": {
                        "id": 1000,
                        "name": "Dogs",
                        "section": {
                            "id": 10,
                            "name": "Pets"
                        }
                    },
                    "date": 1467722851,
                    "thumb_photo": "https://pp.vk.me/c631229/v631229852/3b6e5/1OWGz65-8vw.jpg",
                    "availability": 0
                },
                {
                    "id": 250396,
                    "owner_id": -124527492,
                    "title": "First market item",
                    "description": "Description text",
                    "price": {
                        "amount": "10000",
                        "currency": {
                            "id": 643,
                            "name": "RUB"
                        },
                        "text": "100 rub."
                    },
                    "category": {
                        "id": 1,
                        "name": "Women's Clothing",
                        "section": {
                            "id": 0,
                            "name": "Fashion"
                        }
                    },
                    "date": 1467721947,
                    "thumb_photo": "https://pp.vk.me/c633819/v633819852/37ae0/7lXUEbCwYYM.jpg",
                    "availability": 0
                }
			],
			item : null,
			history: ['items']
		};
	}

	getLocationHash() {
		return window.location.hash.replace('#','')
	}

	getObjectUrlString(string) {
		let search = string
		let objectUrl = search === "" ? null : search.split("&").reduce((prev, curr) => {
			const [key, value] = curr.split("=");
			prev[decodeURIComponent(key)] = decodeURIComponent(value);
			return prev;
		}, {});
		return objectUrl
	}

	getActivePanelRenderString() {
		const stringHash = this.getLocationHash()
		const objectParametrs = this.getObjectUrlString(stringHash)
		let renderStringActivePanel = 'items'

		if (objectParametrs !== null && typeof objectParametrs.itemId !== 'undefined') {
			renderStringActivePanel = 'item'
		}
		return renderStringActivePanel
	}

	componentWillMount() {
		connect.subscribe((e) => {
			switch (e.detail.type) {
				case 'VKWebAppAccessTokenReceived':
					this.setState({ authToken : e.detail.data.access_token }, () =>{
						const stringActivePanel = this.getActivePanelRenderString()
						if (stringActivePanel === 'items') {
							this.setState({
								activePanel: 'items'
							}, () => {
								this.getItems()
							})
						} else if (stringActivePanel.indexOf('item') !== -1) {
							const stringHash = this.getLocationHash()
							const objectParametrs = this.getObjectUrlString(stringHash)
							const id = objectParametrs.itemId
							this.setState({
								activePanel: 'item'
							}, () => {
								this.getInfoItem(id)
							})
						} else {
							this.setState({
								activePanel: 'items'
							}, () => {
								this.getItems()
							})
						}
					});
					break;
				case 'VKWebAppCallAPIMethodResult':
					if (e.detail.data.request_id === "items") {
						this.setState({ items : e.detail.data.response.items })
					}
					if (e.detail.data.request_id === "item") {
						this.setState({ item : e.detail.data.response.items[0] })
					}
					break;
				default:
					console.log(e.detail);
			}
		});
		connect.send("VKWebAppGetAuthToken", {"app_id": 6906999, "scope": "market"});
	}

	getItems() {
		const ownerId = -124527492
		connect.send("VKWebAppCallAPIMethod", {"method": "market.get", "request_id" : "items", "params": {"owner_id": ownerId , "v":"5.92", "access_token": this.state.authToken }});
	}

	getInfoItem(id) {
		const ownerId = -124527492
		let itemId = `${ownerId}_${id}`
		connect.send("VKWebAppCallAPIMethod", {"method": "market.getById", "request_id" : "item",  "params": {"item_ids": itemId , "v":"5.92", "extended" : 1,  "access_token": this.state.authToken }});
	}

	go = (e, object) => {
		let activePanel = e.currentTarget.dataset.to
		this.setState({ activePanel : activePanel })
		if (activePanel === 'items') {
			connect.send("VKWebAppSetLocation", {"location": "items"});
			this.getItems()
		}
		if (activePanel === 'item') {
			connect.send("VKWebAppSetLocation", {"location": `item${object.itemId}`});
			this.getInfoItem(object.itemId)
		}
	}

	render() {
		return (
			<View activePanel={this.state.activePanel}>
				<Items 
					id="items" 
					items={this.state.items}
					go={this.go}
				/>
				<Item 
					id="item"
					item={this.state.item}
					go={this.go}
				/>
			</View>
		);
	}
}

export default App;
