import React from 'react';
import PropTypes from 'prop-types';

import {
	Panel,PanelHeader,
	Button,
	Group, Cell, Div,
	Avatar,
	ModalPageHeader, ModalRoot, ModalCard,
	IS_PLATFORM_ANDROID, IS_PLATFORM_IOS
} from '@vkontakte/vkui';

<ModalPageHeader
  left={(
    <Fragment>
      {IS_PLATFORM_ANDROID && <HeaderButton onClick={this.backModal}><Icon24Cancel /></HeaderButton>}
    </Fragment>
  )}
  right={(
    <Fragment>
      {IS_PLATFORM_ANDROID && <HeaderButton onClick={this.backModal}><Icon24Done /></HeaderButton>}
      {IS_PLATFORM_IOS && <HeaderButton onClick={this.backModal}>Готово</HeaderButton>}
    </Fragment>
  )}
>
  Заголовок модальной страницы
</ModalPageHeader>


const Home = ({ id, go, fetchedUser }) => (
	<Panel id={id}>
		<PanelHeader>vkApp</PanelHeader>
		{fetchedUser &&
		<Group title="Информация о пользователе">
			<Cell
				before={fetchedUser.photo_200 ? <Avatar src={fetchedUser.photo_200}/> : null}
				description={fetchedUser.city && fetchedUser.city.title ? fetchedUser.city.title : ''}
			>
				{`${fetchedUser.first_name} ${fetchedUser.last_name}`}
			</Cell>
		</Group>}

		<Group title="Меню">
			<Div>
				<Cell>
				<Button size="xl" level="1" onClick={go} data-to="persik">
					Покажи мне кису!
				</Button>
				</Cell>
				<Cell>
				<Button size="xl" level="2" onClick={go} data-to="info">
                  Информация
              </Button>
			  </Cell>
			</Div>
		</Group>
	</Panel>
);

Home.propTypes = {
	id: PropTypes.string.isRequired,
	go: PropTypes.func.isRequired,
	fetchedUser: PropTypes.shape({
		photo_200: PropTypes.string,
		first_name: PropTypes.string,
		last_name: PropTypes.string,
		city: PropTypes.shape({
			title: PropTypes.string,
		}),
	}),
};

export default Home;
