import React from 'react';
import connect from '@vkontakte/vk-connect';
import {View} from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';

import Items from './panels/Items';
import Item from './panels/Item';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            activePanel: 'item',
            fetchedUser: null,
            authToken: null,
            items: []
        };

    }


    componentDidMount() {
        connect.subscribe((e) => {
            switch (e.detail.type) {
                case 'VKWebAppGetUserInfoResult':
                    if (e.detail.data.request_id === "items") {
                        this.setState({
                            items: e.detail.data.response.items
                        })
                    }
                    break;
                case 'VKWebAppAccessTokenReceived':
                    this.setState({authToken: e.detail.data.access_token});
                    this.getItems()
                    break;
                default:
                    console.log(e.detail.type);
            }
        });
        connect.send('VKWebAppGetUserInfo', {});
        connect.send('VKWebAppGetAuthToken', {'app_id': 6396978, 'scope': 'market'});
    }

    go = (e) => {
        this.setState({activePanel: e.currentTarget.dataset.to})
    };

    getItems() {
        const ownerId = -124527492
        connect.send('VKWebAppCallAPIMethod', {
            "method": "market.get",
            "request_id": "items",
            "params": {
                "owner_id": ownerId,
                "v": "5.92",
                "access_token": this.state.authToken
            }

        });
    }

    getInfoItem(id) {
        const ownerId = -124527492
        let itemId = '${ownerId}_${id}'
        connect.send("VKWebAppCallAPIMethod", {
            "method": "market.getById",
            "extend": 1,
            "request_id": "item",
            "params": {
                "item_ids": itemId,
                "v": "5.92",
                "access_token": this.state.authToken
            }
        });
    }

    render() {
        return (
            <View activePanel={this.state.activePanel}>
                <Items id="items" items={this.state.items} go={this.go}/>
                <Item id="item" go={this.go} />
            </View>
        );
    }
}

export default App;
