import React from 'react';
import ReactDOM from 'react-dom';

class Time extends React.Component {
	constructor(props) {
		super(props)
		this.state={
			time: null,
			city: null
		}
	}

	componentDidMount() {
		this.timeUpdate()
	}

	timeUpdate = () => setInterval(() => {
		this.setState({time: new Date().toLocaleTimeString()})
	}, 1000);

	render() {
		return (
			<div style={{
				border: "8px solid",
				borderImage: "linear-gradient(-45deg,#743ad5,#d53a9d) 1"
			}}>
				Время:<br/>
				{new Date().toLocaleTimeString()}
			</div>
		)
	}
}

ReactDOM.render(<div><Time city="Москва"/><br/><Time city="Санкт-Петербург"/></div>, document.getElementById('root'));
