import React from 'react';
import PropTypes from 'prop-types';

import {
	Panel, PanelHeader,
	Button, HeaderButton,
	Group, Cell, Div, View,
	Avatar,
	ModalRoot, ModalCard,
	FormLayout, InfoRow,
	ModalPage, ModalPageHeader,
	IS_PLATFORM_IOS, IS_PLATFORM_ANDROID
} from '@vkontakte/vkui';

import Icon56MoneyTransferOutline from '@vkontakte/icons/dist/56/money_transfer_outline';
import Icon24Cancel from '@vkontakte/icons/dist/24/cancel';
import Icon24Dismiss from '@vkontakte/icons/dist/24/dismiss';

const INFO = 'info';
const DOSTAVKA = 'dostavka';

class Home extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      activeModal: null,
      modalHistory: []
    };

    this.modalBack = () => {
      this.setActiveModal(this.state.modalHistory[this.state.modalHistory.length - 2]);
    };
  }

  setActiveModal(activeModal) {
    activeModal = activeModal || null;
    let modalHistory = this.state.modalHistory ? [...this.state.modalHistory] : [];

    if (activeModal === null) {
      modalHistory = [];
    } else if (modalHistory.indexOf(activeModal) !== -1) {
      modalHistory = modalHistory.splice(0, modalHistory.indexOf(activeModal) + 1);
    } else {
      modalHistory.push(activeModal);
    }

    this.setState({
      activeModal,
      modalHistory
    });
  };

  render() {
    const modal =(
      <ModalRoot activeModal={this.state.activeModal}>
        <ModalCard
          id={INFO}
          onClose={() => this.setActiveModal(null)}
          icon={<Icon56MoneyTransferOutline />}
          title="Сошьем вашу худи за три недели!"
          caption="Доставка займёт до 6 рабочих дней"
          actions={[{
            title: 'Подробнее о доставке',
            type: 'primary',
            action: () => {
				this.setActiveModal(DOSTAVKA);
			  }
          }]}
        >
        </ModalCard>
		<ModalPage
			id={DOSTAVKA}
			header={
				<ModalPageHeader
					left={IS_PLATFORM_ANDROID && <HeaderButton onClick={this.modalBack}><Icon24Cancel /></HeaderButton>}
              		right={IS_PLATFORM_IOS && <HeaderButton onClick={this.state}><Icon24Dismiss /></HeaderButton>}
            	>
              		Информация о доставке
            	</ModalPageHeader>
			}
			onClose={() => this.state}
		>
			<Div>
				<InfoRow title='Сроки доставки'>
					Доставим ваш заказ по Санкт-Петербургу до любого адреса в пределах города в тот же день пешими курьерами «Достависта». Из Санкт-Петербурга доставляем ролики, запчасти и аксессуары по любому адресу в пределах России транспортной компанией СДЭК. Но лучше будет воспользоваться доставкой до пункта выдачи, доставка будет дешевле. Доставка занимает от 2 до 6 дней, в зависимости от удалённости от СПб. Если вам необходима доставка за границу России, предложим воспользоваться другой проверенной службой доставки. Если готовите сюрприз или хотите с нами пообщаться, можете забрать заказ самостоятельно из нашего магазина на Лесном пр. 77.
				</InfoRow>
			</Div>
			<Div>
				<InfoRow title='Стоимость доставки'>
					Рассчитываем стоимость доставки при оформлении заказа по тарифам транспортных компаний. Доставка оплачивается отдельно при получении. 
				</InfoRow>
			</Div>
		</ModalPage>
      </ModalRoot>
    );

    return (
      <View activePanel="modals" modal={modal}>
        <Panel id="modals">
          <PanelHeader>vkApp</PanelHeader>

          <Group>
            <FormLayout>
              <Button size="xl" level="secondary" onClick={() => this.setActiveModal(INFO)}>
                  Информация
              </Button>
            </FormLayout>
          </Group>
        </Panel>
      </View>
    );
  }
}



export default Home;
